import React,{ useEffect,useState } from 'react'
import { Link, useLocation,useNavigate,useParams } from 'react-router-dom'
import { Form,Button,Col,Row } from 'react-bootstrap'
import  Loader   from '../components/Loader'
import  Message   from '../components/Message'
import { useDispatch,useSelector } from 'react-redux'
import { listProductDetails, updateProduct } from '../actions/productActions'
import  FormContainer   from '../components/FormContainer'
import { PRODUCT_UPDATE_RESET } from '../constants/productConstants'
import  axios  from 'axios'
import { Carousel } from 'react-bootstrap';





function ProductEditScreen() {
    const navigate = useNavigate()
    const  { id } = useParams();
    // const orderId = match.params.id
    const productId  = id

    // console.log('priduct_id is :',productId)


    const [name,setName] = useState('')
    const [price,setPrice] = useState(0)
    const [brand,setBrand] = useState('')
    const [category,setCategory] = useState('')
    // const [image,setImage] = useState('')
    const [countInStock,setCountInStock] = useState(0)
    const [description,setDescription] = useState("")
    const [uploading,setUploading] = useState(false)

    const [images, setImages] = useState(Array.from({ length: 10 }, () => ''));
    const [carousel, setCarousel] = useState(null);




    const dispatch = useDispatch()

    

    const productDetails = useSelector((state) => state.productDetails)
    const { error,loading,product } = productDetails

    
    const productUpdate = useSelector((state) => state.productUpdate)
    const { error:errorUpdate,loading:loadingUpdate,success:successUpdate} = productUpdate

        
    const userLogin = useSelector((state)=>state.userLogin)
    const { userInfo } = userLogin

    
    useEffect (() => {

        if(successUpdate){
            dispatch({type:PRODUCT_UPDATE_RESET})
            navigate('/admin/productlist')
        }else{
            if(!product.name || product._id !== Number(productId)) {
            dispatch(listProductDetails(productId))
            }else{
                setName(product.name || "")
                setPrice(product.price || 0)
                setBrand(product.brand || "")
                setCategory(product.category || "")
                setDescription(product.description || "")
                // setImage(product.image || "")
                setCountInStock(product.countInStock || 0)
                setImages(product.images || Array.from({ length: 10 }, () => ''));
                // Assuming there is a field like 'carouselImage' in the product model
                setCarousel(product.carousel || null);
            }
        }}
     , [product,productId,navigate,dispatch,successUpdate])
    
    const submitHandeler = (e) => {
        e.preventDefault()
        dispatch(updateProduct({
            _id:productId,
            name,
            price,
            images,
            brand,
            countInStock,
            category,
            description,
            carousel,
            }))

    }

   


    const uploadFileHandler  = async(e,index) =>
     {
        const file = e.target.files[0]
        const formData =new FormData()

        formData.append('image',file)
        formData.append('product_id',productId)
        setUploading(true)

        try{


            const config ={
                headers:{
                    'Content-Type':'multipart/form-data',
                    Authorization  :`Bearer ${userInfo.token}`
                }
            }


            const {data} = await axios.post('/api/products/upload/',
            formData,
            config)

            // setImage(data)
            setImages((prevImages) => {
                const newImages = [...prevImages];
                newImages[index] = data;
                return newImages;
              });9

            setCarousel(data);
            setUploading(false)

        }catch(error){
            console.error('File upload error:', error);
            setUploading(false)
        }
    }



  return (

    <div>

        <Link to='/admin/productlist'>Go Back</Link>

        <FormContainer>
            <h1>Edit Product</h1>
            {loadingUpdate && <Loader />}
            {errorUpdate && <Message variant = 'danger'>{errorUpdate}</Message>}


            {loading ? <Loader />
            :error ?  (<Message variant='danger'>{error}</Message>)
            :(
                <Form onSubmit={ submitHandeler }>
                    <Form.Group controlId='name'>
                        <Form.Label>
                            Name
                        </Form.Label>
                        <Form.Control 
                        type='text'
                        placeholder='Name'
                        value={name}
                        onChange={(e)=>setName(e.target.value)}>

                        </Form.Control>
                    </Form.Group>


                    <Form.Group controlId='price'>
                        <Form.Label>
                            Price
                        </Form.Label>
                        <Form.Control 
                        type='number'
                        placeholder='Enter Price'
                        value={price}
                        onChange={(e)=>setPrice(e.target.value)}>

                        </Form.Control>
                    </Form.Group>














{/* 

                    <Form.Group controlId='image'>
                        <Form.Label>
                            Image
                        </Form.Label>

                        <Form.Control 
                        type='text'
                        placeholder='Enter Image'
                        value={image}
                        onChange={(e)=>setImage(e.target.value)}>

                        </Form.Control>

                        <Form.Control
                        type='file'
                        label ='Choose Image'
                        custom='true'
                        onChange ={ uploadFileHandler}>
                            
                        </Form.Control>
                        {uploading && <Loader />}

                    </Form.Group> 
 */}

<Form.Group controlId='images'>
              <Form.Label>Images</Form.Label>
              {images.map((image, index) => (
                <Row key={index}>
                  <Col>
                    <Form.Control
                      type='text'
                      placeholder={`Enter Image ${index + 1}`}
                      value={image}
                      onChange={(e) => {
                        setImages((prevImages) => {
                          const newImages = [...prevImages];
                          newImages[index] = e.target.value;
                          return newImages;
                        });
                      }}
                    />
                  </Col>
                  <Col>
                    <Form.File
                      id={`image-${index}`}
                      label={`Choose Image ${index + 1}`}
                      custom
                      onChange={(e) => uploadFileHandler(e, index)}
                    />
                    {uploading && <Loader />}
                  </Col>
                </Row>
              ))}
            </Form.Group>




                          {/* Carousel Image Input */}
            <Form.Group controlId='carouselImage'>
              <Form.Label>Carousel Image</Form.Label>
              <Row>
                <Col>
                  <Form.Control
                    type='text'
                    placeholder={`Enter Carousel Image URL`}
                    value={carousel || ''}
                    onChange={(e) => setCarousel(e.target.value)}
                  />
                </Col>
                <Col>
                  <Form.File
                    id={`carousel-image`}
                    label={`Choose Carousel Image`}
                    custom
                    onChange={(e) => uploadFileHandler(e, 'carousel')}
                  />
                  {uploading && <Loader />}
                </Col>
              </Row>
            </Form.Group>

            {/* Display Carousel */}
            <Form.Group controlId='imageCarousel'>
              <Form.Label>Image Carousel</Form.Label>
              <Carousel>
                {/* Display carousel images from state */}
                {images.map((image, index) => (
                  <Carousel.Item key={index}>
                    <img className='d-block w-100' src={image} alt={`Image ${index + 1}`} />
                  </Carousel.Item>
                ))}
                {/* Display carousel image from state */}
                {carousel && (
                  <Carousel.Item>
                    <img className='d-block w-100' src={carousel} alt='Carousel' />
                  </Carousel.Item>
                )}
              </Carousel>
            </Form.Group>
























                    <Form.Group controlId='brand'>
                        <Form.Label>
                            Brand
                        </Form.Label>
                        <Form.Control 
                        type='text'
                        placeholder='brand'
                        value={brand}
                        onChange={(e)=>setBrand(e.target.value)}>

                        </Form.Control>
                    </Form.Group>

                    <Form.Group controlId='countinstock'>
                        <Form.Label>
                            Stock
                        </Form.Label>
                        <Form.Control 
                        type='number'
                        placeholder='Stock'
                        value={countInStock}
                        onChange={(e)=>setCountInStock(e.target.value)}>

                        </Form.Control>
                    </Form.Group>

                    <Form.Group controlId='category'>
                        <Form.Label>
                            Category
                        </Form.Label>
                        <Form.Control 
                        type='text'
                        placeholder='Category'
                        value={category}
                        onChange={(e)=>setCategory(e.target.value)}>

                        </Form.Control>
                    </Form.Group>

                    <Form.Group controlId='description'>
                        <Form.Label>
                           Description
                        </Form.Label>
                        <Form.Control 
                        type='text'
                        placeholder='Description'
                        value={description}
                        onChange={(e)=>setDescription(e.target.value)}>

                        </Form.Control>
                    </Form.Group>


                    <br></br>

                    <Button 
                    type='submit'
                    variant='primary' >
                        Update
                    </Button>


                </Form>

            )}
        </FormContainer>
    </div>
  )
}

