import { 
    ORDER_CREATE_REQUEST,
    ORDER_CREATE_SUCCESS,
    ORDER_CREATE_FAILE,

    ORDER_CREATE_RESET,


    ORDER_DETAILS_REQUEST,
    ORDER_DETAILS_SUCCESS,
    ORDER_DETAILS_FAILE,

    ORDER_PAY_REQUEST,
    ORDER_PAY_SUCCESS,
    ORDER_PAY_FAILE,
    ORDER_PAY_RESET,


    ORDER_LIST_MY_REQUEST,
    ORDER_LIST_MY_SUCCESS,
    ORDER_LIST_MY_FAILE,
    ORDER_LIST_MY_RESET,


    ORDER_LIST_REQUEST,
    ORDER_LIST_SUCCESS,
    ORDER_LIST_FAILE,


    ORDER_DELIVERED_REQUEST,
    ORDER_DELIVERED_SUCCESS,
    ORDER_DELIVERED_FAILE,
    ORDER_DELIVERED_RESET,} from '../constants/OrderConstant'




export const orderCreateReducer = (state={},action) => {
    switch (action.type){
        case ORDER_CREATE_REQUEST:
            return {
                loading:true,
            }
        case ORDER_CREATE_SUCCESS:
            return {
                loading:false,
                success:true,
                order:action.payload,
            }
        case ORDER_CREATE_FAILE:
            return {
                loading:false,
                error:action.payload,
            }
        case ORDER_CREATE_RESET:
            return {  }       // RESETING THE CART AFTER SUCCESSFULL SUBMITION OF ORDER
        default:
            return state
    }
}






export const orderDetailsReducer = (state={loading:true,order:[],shippingAddress:{}},action) => {
    switch (action.type){
        case ORDER_DETAILS_REQUEST:
            return {
                ...state,
                loading:true,
            }
        case ORDER_DETAILS_SUCCESS:
            return {
                loading:false,
                order:action.payload,
                error:null,
            }
        case ORDER_DETAILS_FAILE:
            return {
                loading:false,
                error:action.payload,
            }
        default:
            return state
    }
}






export const orderPayReducer = (state={},action) => {
    switch (action.type){
        case ORDER_PAY_REQUEST:
            return {
                loading:true,
            }
        case ORDER_PAY_SUCCESS:
            return {
                loading:false,
                success:true,
            }
        case ORDER_PAY_FAILE:
            return {
                loading:false,
                error:action.payload,
            }
        case ORDER_PAY_RESET:
            return {}
        default:
            return state
    }
}






export const ordeDeliveredReducer = (state={},action) => {
    switch (action.type){
        case ORDER_DELIVERED_REQUEST:
            return {
                loading:true,
            }
        case ORDER_DELIVERED_SUCCESS:
            return {
                loading:false,
                success:true,
            }
        case ORDER_DELIVERED_FAILE:
            return {
                loading:false,
                error:action.payload,
            }
        case ORDER_DELIVERED_RESET:
            return {}
            
        default:
            return state
    }
}




export const orderListMyReducer = (state={orders:[]},action) => {
    switch (action.type){
        case ORDER_LIST_MY_REQUEST:
            return {
                loading:true,

            }
        case ORDER_LIST_MY_SUCCESS:
            return {
                loading:false,
                orders:action.payload,
            }
        case ORDER_LIST_MY_FAILE:
            return {
                loading:false,
                error:action.payload,
            }
        case ORDER_LIST_MY_RESET:
            return {
                orders:[]
            }
        default:
            return state
    }
}








export const orderListReducer = (state={orders:[]},action) => {
    switch (action.type){
        case ORDER_LIST_REQUEST:
            return {
                loading:true,

            }
        case ORDER_LIST_SUCCESS:
            return {
                loading:false,
                orders:action.payload,
            }
        case ORDER_LIST_FAILE:
            return {
                loading:false,
                error:action.payload,
            }

        default:
            return state
    }
}
