import { 
    USER_LOGIN_REQUEST,
    USER_LOGIN_SUCCESS,
    USER_LOGIN_FAILE,


    USER_LOGOUT,

    USER_REGISTER_REQUEST,
    USER_REGISTER_SUCCESS,
    USER_REGISTER_FAILE,

    USER_DETAILS_REQUEST,
    USER_DETAILS_SUCCESS,
    USER_DETAILS_FAILE,
    USER_DETAILS_RESET,

    USER_UPDATE_PROFILE_REQUEST,
    USER_UPDATE_PROFILE_SUCCESS,
    USER_UPDATE_PROFILE_FAILE,

    USER_LIST_REQUEST,
    USER_LIST_SUCCESS,
    USER_LIST_FAILE,
    USER_LIST_RESET, 

    USER_DELETE_REQUEST,
    USER_DELETE_SUCCESS,
    USER_DELETE_FAILE,


    USER_UPDATE_REQUEST,
    USER_UPDATE_SUCCESS,
    USER_UPDATE_FAILE,
    USER_UPDATE_RESET,} from '../constants/userConstants'
import axios  from 'axios'


import { ORDER_LIST_MY_RESET } from '../constants/OrderConstant'

export  const  login = (email,password) => async (dispatch) => {
    try{
        dispatch({
            type:USER_LOGIN_REQUEST,
    })
    const config = {
        headers:{
            'Content-type':'application/json'
        }

    }
    const {data} = await axios.post(
        '/api/users/login',
        {'username':email,'password':password},config
        )
    
    dispatch({
        type:USER_LOGIN_SUCCESS,
        payload:data,
    })


    localStorage.setItem('userInfo',JSON.stringify(data))

    }
    catch(error){
        dispatch({
            type:    USER_LOGIN_FAILE,
            payload:error.response && error.response.data.detail
            ?
            error.response.data.detail:
            error.detail,
        })
    }
}



export const logout = () => (dispatch) => {
    localStorage.removeItem('userInfo')
    dispatch({
        type: USER_LOGOUT,
    })
    dispatch({
        type: USER_DETAILS_RESET,
    })
    dispatch({
        type: ORDER_LIST_MY_RESET,
    })
    dispatch({
        type: USER_LIST_RESET,
    })
}




export  const  register = ( name,email,password) => async (dispatch) => {
    try{
        dispatch({
            type:USER_REGISTER_REQUEST,
    })
    const config = {
        headers:{
            'Content-type':'application/json'
        }

    }
    const {data} = await axios.post(
        '/api/users/register/',
        {'name':name,'email':email,'password':password},config
        )
    
    dispatch({
        type:USER_REGISTER_SUCCESS,
        payload:data,
    })
    dispatch({
        type:USER_LOGIN_SUCCESS,
        payload:data,
    })

    localStorage.setItem('userInfo',JSON.stringify(data))

    }
    catch(error){
        dispatch({
            type:    USER_REGISTER_FAILE,
            payload:error.response && error.response.data.detail
            ?
            error.response.data.detail:
            error.detail,
        })
    }
}




export  const  getUserDetails = ( id ) => async (dispatch,getState) => {
    try{
        dispatch({
            type:USER_DETAILS_REQUEST,
    })

    const {
        userLogin : { userInfo },
    }=getState()



    const config = {
        headers:{
            'Content-type':'application/json',
            Authorization  :`Bearer ${userInfo.token}`
        }

    }
    const {data} = await axios.get(
        `/api/users/${id}/`,
        config
        )
    
    dispatch({
        type:USER_DETAILS_SUCCESS,
        payload:data,
    })
    

    }
    catch(error){
        dispatch({
            type:    USER_DETAILS_FAILE,
            payload:error.response && error.response.data.detail
            ?
            error.response.data.detail:
            error.detail,
        })
    }
}



export  const  updateUserProfile = ( user ) => async (dispatch,getState) => {
    try{
        dispatch({
            type:USER_UPDATE_PROFILE_REQUEST,
    })

    const {
        userLogin : { userInfo },
    }=getState()



    const config = {
